    Producto p;
    Vector productos = new Vector();
    int total, codigo;
        
    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // Evento y código a ejecutarse al presionar clic sobre el botón
        int codigo = Integer.parseInt(txtCodigo1.getText().toString());
        String nombre = txtNombre.getText().toString();
        double precio = Double.parseDouble(txtPrecio.getText().toString());
        int stock = Integer.parseInt(txtStock.getText().toString());
        p = busqueda(codigo, productos, total);
        if(p==null){
           productos.addElement(new Producto(codigo,nombre,precio,stock));
           javax.swing.JOptionPane.showMessageDialog(null,"\nProducto agregado");
           total++;
        }
        else
           javax.swing.JOptionPane.showMessageDialog(null,"\nYa existe este producto");
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // Evento y código a ejecutarse al presionar clic sobre el botón
        codigo = Integer.parseInt(txtCodigo2.getText().toString());
        p = busqueda(codigo, productos, total);
        if(p!=null){
           if(p.getStock() > 0){ 
              p.setStock(p.getStock() - 1);
              javax.swing.JOptionPane.showMessageDialog(null,"\nVenta realizada");
              p.mostrarResultadoGrafico();
           }
           else
              javax.swing.JOptionPane.showMessageDialog(null,"\nSin stock");
        }else 
              javax.swing.JOptionPane.showMessageDialog(null,"\nProducto inexistente");
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // Evento y código a ejecutarse al presionar clic sobre el botón
        codigo = Integer.parseInt(txtCodigo3.getText().toString());
        p = busqueda(codigo, productos, total);
        if(p!=null)
           p.mostrarResultadoGrafico();
        else 
           javax.swing.JOptionPane.showMessageDialog(null,"\nProducto inexistente");    
    }//GEN-LAST:event_jButton3ActionPerformed

