    static Producto busqueda(int codigo, Vector productos, int total){
        boolean band = false;
        Producto p,retornP = null;
        for(int x = 0; x < total; x++){ 
            p = (Producto) productos.elementAt(x);
            if(p.getCodigo() == codigo)
                retornP = p;
        }
        return retornP;
    }        
            
